package Assignment1;

public class LargestNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10, b=40,c=30;
		if(a>b && a>c) {
			System.out.println(a+" is greater");
		}
		else if(b>c) {
			System.out.println(b+" is greater");
		}
		else {
			System.out.println(c+" is greater");
		}
	}

}
