package Assignment1;

public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1=234; int n2=567; int temp;
		System.out.println("Before swapping 1st number = "+n1+" and second number = "+n2);
		temp=n1;
		n1=n2;
		n2=temp;
		
		System.out.println("After swapping 1st number = "+n1+" and second number = "+n2);
	}

}
