package Assignment1;

public class Week {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int d=7;
		switch(d) {
		case 1:System.out.println(d+"st day is Sunday");
		break;
		case 2:System.out.println(d+"nd day is Monday");
		break;
		case 3:System.out.println(d+"rd day is Tuesday");
		break;
		case 4:System.out.println(d+"th day is Wednesday");
		break;
		case 5:System.out.println(d+"th day is Thursday");
		break;
		case 6:System.out.println(d+"th day is Friday");
		break;
		case 7:System.out.println(d+"th day is Saturday");
		break;
		default:System.out.println("Enter valid number");
		}

	}

}
