package january18;

public class AirthmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10, y=30;
		
		System.out.println(x+y);
		System.out.println("Hello"+" World");
		System.out.println("Subtract: "+(y-x));
		System.out.println("Division: "+(y/x));
		System.out.println("Remainder: "+(y%x));
		System.out.println((x++)+"First prints then increments");
		System.out.println(y--);
		System.out.println(x);
		System.out.println(y);
		System.out.println((++x)+"Increments and then prints");
		System.out.println(--y);
	}

}
