package january19;

public class Greater {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20,b=22,c=23;
		if(a>b&&a>c) {
			System.out.println("A is greater");
		}
		else if(b>c) {
			System.out.println("B is greater");
		}
		else {
			System.out.println("C is greater");
		}

	}

}
